function ClearClassFromElement(){
    var element = document.getElementsByClassName("read-mode");
    var element1 = document.getElementsByClassName("ql-editor");
    var element2 = document.getElementsByClassName("nav-avatar");
    if (element[0]){
        element[0].classList.remove("read-mode");
        element1[0].classList.remove("ql-editor");
    }
    if(element2[0]){
        element2[0].classList.remove("nav-avatar");
    }
    // element2[0].classList.add("row");
    // console.log(element2)
}


window.onload = ClearClassFromElement();

